import React, { useEffect, useState, useContext } from "react"
import styles from "../css/accessForm.module.css"

import EmailValidator from "./emailValidator.js"

import StoreRMDb from "./storeRMDb.js"
import storeRMBrowser from "./storeRMBrowser.js"

import Login from "./login.js"

import AuthContext from "/home/ubuntu/dev/src/js/AuthContext.js"

import { CSSTransition } from "react-transition-group"

import "../css/accessForm.css"

export default function AccessForm() {

	const transitionDurationMs = 500

	/*
	Using Context to update hook state in RoutesV2.js
	to detect when user has successfully logged in
	*/
	const { setAuthenticated } = useContext( AuthContext )
	const setAuth = () => setAuthenticated( false )

	/* Hooks */

	// Store Registration (password) Notification element's height
	const [regNotifHeight, setRegNotifHeight] = useState( 0.0 )
	useEffect( () => {
		console.log( "HEIGHT: " + regNotifHeight )
	}, [regNotifHeight] )

	// Store movable elements' original Y-axis value
	const [origYPos, setOrigYPos] = useState(
		[
			0.0,
			0.0,
			0.0
		]
	)
	useEffect( () => {
		console.log( origYPos.length )
	}, origYPos )

	// Executed on component mount
	useEffect( () => {

		// Get Registration Notification -element's height in px
		setRegNotifHeight( document.querySelector( "#regNotif" ).getBoundingClientRect().height )


		const inputs = document.querySelectorAll( ".inputContainer" )
		inputs.forEach( ( input, index ) => {
			const arr = [...origYPos]
			arr[index] = input.offsetTop
			setOrigYPos( arr )
			input.style.top = input.offsetTop
		} )
	}, [] )

	// Change between login and registration
	const [loginMode, setLoginMode] = useState( -1 )

	// Store valid email address
	const [email, setEmail] = useState( "" )
	useEffect( () => {
		email.length > 0
			? console.log( "Email address set to: " + email )
			: console.log( "Email address removed" )
	}, [email] )

	// Store valid password
	const [password, setPassword] = useState( "" )


	/* Form input handlers */

	const HandleEmail = ( e ) => {
		const email = e.target.value

		EmailValidator( email ) ?
			setEmail( email ) :
			setEmail( "" )
	}

	const HandlePassword = ( e ) => {
		const password = e.target.value

		console.log( "Password length: " + password.length )

		password.length >= 8 ? setPassword( password ) : setPassword( "" )
	}

	/* Handle form submit */

	const Validate = async ( e ) => {

		e.preventDefault()

		// First value in multiplier chain is the amount of days
		const timestamp = Date.now() + ( 7 * 24 * 60 * 60 * 1000 )

		new Promise( resolve => {
			resolve( Login( email, password ) )
		} ).then( res => {
			res.data === 1 && new Promise( resolve => {
				resolve( StoreRMDb( email, timestamp ) )
			} ).then( res => {
				if ( res.data.length > 1 && res.data !== "0" ) {
					console.log( "Cookie stored successfully into database" )
					if ( storeRMBrowser( res.data ) ) {
						setAuth( true )
					}
				}
			} )
		} )
	}

	const MoveFormItem = ( element, animateUp, yDiff ) => {

		// const animationTimeMs = 3000

		// element.classList.add( "verticalAnim" )

		// setTimeout( () => {
		// 	element.classList.remove( "verticalAnim" )
		// }, animationTimeMs )
	}

	return (
		< div className={styles.container} >
			<div className={styles.formContainer}>
				<form onSubmit={( e ) => Validate( e )}>
					<div
						className={styles.inputContainer}
						style={
							{
								position: "absolute",
								transform: ( loginMode != -1 && loginMode == 1 ) ? `translateY(${regNotifHeight}px)` : `translateY(-${regNotifHeight}px)`,
								transitionDuration: transitionDurationMs
							}
						} >
						<input
							type="email"
							name="email"
							id="email"
							placeholder="Email"
							onChange={( e ) => {
								HandleEmail( e )
							}}
						></input>
					</div>
					<div
						className={styles.inputContainer}
						style={
							{
								position: "absolute",
								transform: ( loginMode != -1 && loginMode == 1 ) ? `translateY(${regNotifHeight}px)` : `translateY(-${regNotifHeight}px)`,
								transition: `${transitionDurationMs}ms ease-in-out 0s`
							}
						}>
						<input
							type="password"
							name="password"
							id="password"
							placeholder="Password"
							onChange={( e ) => {
								HandlePassword( e )
							}}
						></input>
					</div>

					<div id="regNotif" style={
						{
							fontFamily: "Open Sans Light",
							fontSize: "12px",
							lineHeight: "15px",
							textAlign: "center",
							marginBottom: "10px",
							opacity: ( !loginMode ? "1" : "0.01" ),
							position: "absolute"
						}}>
						Password must be at least 8 characters long
					</div>

					<div
						className={styles.inputContainer}
						style={
							{
								position: "absolute",
								transform: ( loginMode != -1 && loginMode == 1 ) ? `translateY(-${regNotifHeight}px)` : `translateY(${regNotifHeight}px)`,
								transition: `${transitionDurationMs}ms ease-in-out 0s`
							}
						}>
						<input
							type="submit"
							disabled={
								email.length > 0 && password.length > 0
									? false
									: true
							}
							value={loginMode ? "Login" : "Register"}
						></input>
					</div>
				</form>
			</div>

			<button
				onClick={() =>
					loginMode == -1 || loginMode == 1 ?
						setLoginMode( 0 ) :
						setLoginMode( 1 )
				}
			>
				{loginMode
					? "Don't have an account?\nClick here to register"
					: "Already have an account?\nClick here to login"}
			</button>
		</div >
	)
}
