import JsCookie from "js-cookie"
import VerifyCookie from "../php/verifyCookie.php"

import Axios from "axios"

export default function CheckCookie() {

    if ( JsCookie.get( 'rememberMe' ) === undefined ) return false

    // Post 'Remember Me' -cookie's content to PHP-script
    Axios( {
        method: "post",
        url: "http://www.paste.services/login/verifyCookie",
        headers: {
            'Content-Type': 'application/json; charset=UTF-8'
        },
        data: {
            "hash": JsCookie.get('rememberMe')
        },
    } ).then( ( res ) => {

        console.log( "Response: " + res.data )

        // Return true if successfully logged in
        return res.data === "1"
    } )

    // Return true if 'Remember Me' -cookie matches any
    // stored in the database, otherwise false
    return VerifyCookie( JsCookie.get( 'rememberMe' ) )
}
