import React, { useState } from "react"
import CheckCookie from "/home/ubuntu/dev/src/js/checkCookie.js"
import { Redirect } from "react-router";

export default function Splash( props ) {

    const [cookieFound, setCookieFound] = useState( false )
    const [promiseFinished, setPromiseFinished] = useState( false )

    new Promise( resolve => {
        resolve( CheckCookie() )
    } ).then( res => {
        res.data === 1 && setCookieFound( true )
        setPromiseFinished( true )
    } )

    return (
        <div className="container">
            Splash
            { () => {
                if ( promiseFinished ) {
                    cookieFound ?
                        ( () => {
                            props.setLoggedIn( true )
                            return <Redirect to="/frontpage" />
                        } )() :
                        ( () => {
                            return <Redirect to="/access" />
                        } )()
                }
            }
            }
        </div >
    )
}
