import React, { useEffect, useState, useContext } from "react"
import styles from "../css/accessForm.module.css"

import EmailValidator from "../utils/emailValidator.js"

import StoreRMDb from "../utils/storeRMDb.js"
import storeRMBrowser from "../utils/storeRMBrowser.js"

import Login from "../utils/login.js"

import AuthContext from "/home/ubuntu/dev/src/js/AuthContext.js"

export default function AccessForm() {

	const transitionDurationMs = 500

	/* References to some elements to get their dimensions */
	const [
		regNotif,
		inputFields,
	] = [...Array( 2 )].map( React.useRef )

	/*
	Using Context to update hook state in RoutesV2.js
	to detect when user has successfully logged in
	*/
	const { setAuthenticated } = useContext( AuthContext )
	const setAuth = () => setAuthenticated( false )

	/* Hooks */

	const [regNotifHeight, setRegNotifHeight] = useState( 0.0 )
	useEffect( () => {
		console.log("Reg height: " + regNotifHeight)
	}, [regNotifHeight])
	const [inputFieldsWidth, setInputFieldsWidth] = useState( 0.0 )

	// Change between login and registration (modes)
	const [loginMode, setLoginMode] = useState( -1 )

	// Store valid email address
	const [email, setEmail] = useState( "" )
	useEffect( () => {
		email.length > 0
			? console.log( "Email address set to: " + email )
			: console.log( "Email address removed" )
	}, [email] )

	// Store valid password
	const [password, setPassword] = useState( "" )

	const [rememberMe, setRememberMe] = useState( false )


	/* Form input handlers */

	const HandleEmail = ( e ) => {
		const email = e.target.value

		EmailValidator( email ) ?
			setEmail( email ) :
			setEmail( "" )
	}

	const HandlePassword = ( e ) => {
		const password = e.target.value

		console.log( "Password length: " + password.length )

		password.length >= 8 ? setPassword( password ) : setPassword( "" )
	}

	/*
	Code inside this useEffect will be executed on component mount
	*/
	useEffect( () => {

		setRegNotifHeight( regNotif.offsetHeight )
		setInputFieldsWidth( inputFields.offsetWidth )
		// eslint-disable-next-line
	}, [] )

	/* Handle form submit */

	const Validate = async ( e ) => {

		e.preventDefault()

		// First value in multiplier chain is the amount of days
		const timestamp = Date.now() + ( 7 * 24 * 60 * 60 * 1000 )

		new Promise( resolve => {
			resolve( Login( email, password ) )
		} ).then( res => {
			res.data === 1 && new Promise( resolve => {
				resolve( StoreRMDb( email, timestamp ) )
			} ).then( res => {
				if ( res.data.length > 1 && res.data !== "0" ) {
					console.log( "Cookie stored successfully into database" )
					if ( storeRMBrowser( res.data ) ) {
						setAuth( true )
					}
				}
			} )
		} )
	}

	return (
		< div className={styles.container} >
			<div className={styles.formContainer}>
				<form onSubmit={( e ) => Validate( e )}>
					<div
						className={styles.formInputsContainer}
						ref={inputFields}
						style={
							{
								transform: ( loginMode !== -1 && ( loginMode ? `translateY(0px)` : `translateY(-${regNotifHeight}px)` ) ),
								transition: `${transitionDurationMs}ms ease-in-out 0s`
							}
						}>
						<input
							type="email"
							name="email"
							id="email"
							placeholder="Email"
							onChange={( e ) => {
								HandleEmail( e )
							}}
						></input>
						<input
							type="password"
							name="password"
							id="password"
							placeholder="Password"
							onChange={( e ) => {
								HandlePassword( e )
							}}>
						</input>
					</div>

					<div
						className={styles.regNotif}
						ref={regNotif}
						style={
							{
								width: `${inputFieldsWidth}px`,
								opacity: ( loginMode ? 0.01 : 1 ),
								// Add delay to fade-in when switching to registration mode
								transitionDelay: ( !loginMode && "250ms" ),
								transition: `opacity ${transitionDurationMs / 2}ms ease-in-out 0s`
							}
						}>
						Password must be at least 8 characters long
					</div>

					<input
						type="submit"
						disabled={
							email.length > 0 && password.length > 0 ? false : true
						}
						value={loginMode ? "Login" : "Register"}
						style={
							{
								transform: ( loginMode !== -1 && ( loginMode ? `translateY(0px)` : `translateY(${regNotifHeight}px)` ) ),
								// Specifying transform inside transition makes the animation
								// apply only to the transform attribute
								// https://stackoverflow.com/a/21323370/12314897
								transition: `transform ${transitionDurationMs}ms ease-in-out 0s`
							}
						}>
					</input>
				</form>
			</div>

			<div className={styles.checkBoxContainer}>
				<label>
					<input
						type="checkbox"
						defaultChecked={rememberMe}
						onChange={() => { setRememberMe( rememberMe ? false : true ) }}
					/>
					<span>
						Keep me logged in
						</span>
				</label>
			</div>

			<button
				className={styles.modeToggleButton}
				onClick={() =>
					loginMode === -1 || loginMode === 1 ?
						setLoginMode( 0 ) :
						setLoginMode( 1 )
				}>
				{
					loginMode ?
						"Don't have an account?\nClick here to register" :
						"Already have an account?\nClick here to login"
				}
			</button>
		</div >
	)
}
