import React, { useEffect } from "react"

export default async function CheckLogin( email, password ) {

    const HttpRequest = ( email, password ) => {

        let data = new FormData();
        data.append( 'email', email )
        data.append( 'password', password )

        // const xhr = new XMLHttpRequest()

        // xhr.open( 'POST', ( !process.env.NODE_ENV || process.env.NODE_ENV === 'development' ? 'http://localhost:80/login/check' : 'http://paste.services/login/check' ), true )
        // xhr.setRequestHeader("Access-Control-Allow-Origin", true)
        // xhr.setRequestHeader( 'Content-Type', 'multipart/form-data' )

        // xhr.addEventListener( 'load', () => {
        //     xhr.readyState == 4 && xhr.status == 200 && console.log( "XHR: " + xhr.responseText )
        // } )

        // xhr.send( data )

        const options = {
            method: 'POST',
            headers: { 'Content-Type': 'text/plain' },
            body: data
        }

        fetch( '/login/check', options )
            .then( response => {
                return response
            } )
    }

    // Execute when component is called
    useEffect( () => {
        HttpRequest( email, password )
    }, [] )
}
