/*
Code almost perfect copy from the below StackOverflow answer:
https://stackoverflow.com/a/31061352
*/

import { publicEncrypt, privateDecrypt } from "crypto";
import { resolve } from "path";
import { readFileSync } from "fs";

export default function Crypto( toDo, content ) {

    const encryptStringWithRsaPublicKey = ( toEncrypt ) => {
        var absolutePath = resolve( '../custom.pub' )
        var publicKey = readFileSync( absolutePath, "utf8" )
        var buffer = Buffer.from( toEncrypt )
        var encrypted = publicEncrypt( publicKey, buffer )
        return encrypted.toString( "base64" )
    }

    const decryptStringWithRsaPrivateKey = ( toDecrypt ) => {
        var absolutePath = resolve( '../custom' )
        var privateKey = readFileSync( absolutePath, "utf8" )
        var buffer = Buffer.from( toDecrypt, "base64" )
        var decrypted = privateDecrypt( privateKey, buffer )
        return decrypted.toString( "utf8" )
    }

    toDo === 'encrypt' ?
        encryptStringWithRsaPublicKey( content ) :
        decryptStringWithRsaPrivateKey( content )
}
