import JsCookie from "js-cookie"
import Axios from "axios"

export default function CheckCookie() {

    console.log("CheckCookie")

    if ( JsCookie.get( 'rememberMe' ) === undefined ) return false

    // Post 'Remember Me' -cookie's content to PHP-script
    Axios( {
        method: "post",
        url: "http://www.paste.services/access/verifyCookie",
        headers: {
            'Content-Type': 'application/json; charset=UTF-8'
        },
        data: {
            "hash": JsCookie.get( 'rememberMe' )
        },
    } ).then( ( res ) => {

        console.log(
            res.data === 1 ?
                "Existing cookie found" :
                "No existing cookies found"
        )

        // Return true if matching 'Remember Me' -cookie
        // found in the database, otherwise false
        return res.data === 1
    } )
}
