import React, {useEffect, useState} from "react"
import styles from "./loginform.module.css"
import EmailValidator from "./EmailValidator.js"
import LoginChecker from "./CheckLogin.js"

export default function LoginForm() {
	useEffect(() => {
		const form = document.forms[0]
		const emailAddress = form.querySelector( 'input[name="email"]' )
		emailAddress = ''
	}, [])

	const [loginMode, setLoginMode] = useState(true)
	const [email, setEmail] = useState("")
	useEffect(() => {
		email.length > 0
			? console.log("Email address set to: " + email)
			: console.log("Email address removed")
	}, [email])

	const [password, setPassword] = useState("")

	const HandleEmail = (e) => {
		const email = e.target.value

		EmailValidator(email) && setEmail(email)
	}

	const HandlePassword = (e) => {
		const password = e.target.value

		console.log("Password length: " + password.length)

		password.length >= 8 ? setPassword(password) : setPassword("")
	}

	return (
		<div className={styles.container}>
			<div className={styles.formContainer}>
				<form action="" method="post">
					<input
						type="email"
						name="email"
						id="email"
						placeholder="Email"
						onChange={(e) => {
							HandleEmail(e)
						}}
					></input>
					<input
						type="password"
						name="password"
						id="password"
						placeholder="Password"
						onChange={(e) => {
							HandlePassword(e)
						}}
					></input>
					<input
						type="submit"
						disabled={
							email.length > 0 && password.length > 0
								? false
								: true
						}
						value={loginMode ? "Login" : "Register"}
						onClick={() =>
							LoginChecker(email, password).then((response) =>
								console.log("Response: " + response)
							)
						}
					></input>
				</form>
			</div>

			<button
				onClick={() =>
					loginMode ? setLoginMode(false) : setLoginMode(true)
				}
			>
				{loginMode
					? "Don't have an account?\nClick here to register"
					: "Already have an account?\nClick here to login"}
			</button>
		</div>
	)
}
