import React, { useState, useEffect, useContext } from "react"
import CheckCookie from "/home/ubuntu/dev/src/js/checkCookie.js"
import { Redirect } from "react-router";

import AuthContext from "/home/ubuntu/dev/src/js/AuthContext.js"

export default function Splash() {

    console.log( "SPLASH" )

    const { authenticated } = useContext( AuthContext )
    const setAuthenticated = () => setAuthenticated( true )

    const [cookieFound, setCookieFound] = useState( null )

    useEffect( () => {
        new Promise( resolve => {
            resolve( CheckCookie() )
        } ).then( res => {
            setCookieFound( res.data === 1 )
        } )
    }, [] )

    return (
        <div className="container">
            Splash
            {
                ( () => {
                    if ( cookieFound === true ) {
                        setAuthenticated()
                    } else if ( cookieFound = false ) {
                        return <Redirect to="/access" />
                    }
                } )()
            }
        </div >
    )
}
