import React, { useEffect, useState } from "react"
import styles from "./accessForm.module.css"
import Axios from "axios"
import JsCookie from "js-cookie"

import EmailValidator from "../emailValidator.js"
import CreateCookie from "./createCookie.js"

export default function AccessForm() {

	const [loginMode, setLoginMode] = useState( true )
	const [email, setEmail] = useState( "" )
	useEffect( () => {
		email.length > 0
			? console.log( "Email address set to: " + email )
			: console.log( "Email address removed" )
	}, [email] )

	const [password, setPassword] = useState( "" )

	const HandleEmail = ( e ) => {
		const email = e.target.value

		EmailValidator( email ) && setEmail( email )
	}

	const HandlePassword = ( e ) => {
		const password = e.target.value

		console.log( "Password length: " + password.length )

		password.length >= 8 ? setPassword( password ) : setPassword( "" )
	}

	const Validate = ( e ) => {

		e.preventDefault()

		Axios( {
			method: "post",
			url: "http://www.paste.services/login/check",
			headers: {
				'Content-Type': 'application/json; charset=UTF-8'
			},
			data: {
				"email": email,
				"password": password,
			},
		} ).then( ( res ) => {

			// Successfully logged in
			if ( res.data == "OK" ) {

				// If cookie is successfully updated to database, save
				// it in user's computer/browser
				CreateCookie( email ) &&
					JsCookie.set( "rememberMe", email, { expires: 7 } )
			}
			console.log( "Response: " + res.data )
		} )
	}

	return (
		<div className={styles.container}>
			<div className={styles.formContainer}>
				<form onSubmit={( e ) => Validate( e )}>
					<input
						type="email"
						name="email"
						id="email"
						placeholder="Email"
						onChange={( e ) => {
							HandleEmail( e )
						}}
					></input>
					<input
						type="password"
						name="password"
						id="password"
						placeholder="Password"
						onChange={( e ) => {
							HandlePassword( e )
						}}
					></input>
					<input
						type="submit"
						disabled={
							email.length > 0 && password.length > 0
								? false
								: true
						}
						value={loginMode ? "Login" : "Register"}
					></input>
				</form>
			</div>

			<button
				onClick={() =>
					loginMode ? setLoginMode( false ) : setLoginMode( true )
				}
			>
				{loginMode
					? "Don't have an account?\nClick here to register"
					: "Already have an account?\nClick here to login"}
			</button>
		</div>
	)
}
