export default function MoveFormItem( element, animateUp, yDiff ) {

    const animationTimeMs = 3000

    let verticalAnimation = {
        transition: `${animationTimeMs}ms`,
        top: ( animateUp ?
            element.top - yDiff :
            element.top + yDiff
        )
    }

    element.classList.add( verticalAnimation )

    setTimeout( () => {
        element.classList.remove( verticalAnimation )
    }, animationTimeMs )
}
